import { useState } from "react";
function Weather(){
    const[startdate,setStartDate] = useState("");
    const[enddate,setEndDate] = useState("");
    const[error,setError] = useState("");
    const[inputError,setInputError] = useState("");

    const JSON = [
        {
            "day":0,
            "temp":68,
            "image":{
                "src":""
            },
            "message":"WINDY"
        },
        {
            "day":1,
            "temp":74,
            "image":{
                "src":""
            },
            "message":"SUNNY"
        },{
            "day":2,
            "temp":83,
            "image":{
                "src":""
            },
            "message":"THUNDERSTORMS"
        },{
            "day":3,
            "temp":75,
            "image":{
                "src":""
            },
            "message":"MOSTLY CLOUDY"
        },{
            "day":4,
            "temp":82,
            "image":{
                "src":""
            },
            "message":"PARTIALLY CLOUDY"
        },
        {
            "day":5,
            "temp":81,
            "image":{
                "src":""
            },
            "message":"RAIN"
        },{
            "day":6,
            "temp":90,
            "image":{
                "src":""
            },
            "message":"sUNNY"
        }
    ]
// to handle startdate event
    const handleStartDate =  (e)=>{
        setStartDate(e.target.value);
        setError("");
    }

 // to handle enddate event  
    const handleEndDate =  (e)=>{
        setEndDate(e.target.value);
        setError("");
    }

    const handleSubmit = (e)=>{
        e.preventDefault();
        if(startdate==="" || enddate===""){
            setError("Please enter all the fields");
        } else{
            setError("");
            let start = new Date(startdate);
            let end =  new Date(enddate);
            let diffinms = end-start;
            let diffindays = diffinms/(1000*60*60*24);
            console.log(diffindays);
            if(diffindays!==7){
                setInputError("inputs difference should be 7 days");
            } else{
                setInputError("");
                const filteredData = JSON.filter((item)=>{
                    return item.day>=start.getDay() && item.day<=end.getDay()
                })
            }
        }
    }
    return(
        <div>
            <form onSubmit={handleSubmit}>
                <label>Start Date:</label>&nbsp;&nbsp;
                <input type="date" value={startdate} onChange={handleStartDate}/><br/><br/>
                <label> End Date:</label>&nbsp;&nbsp;
                <input type="date" value={enddate} onChange={handleEndDate}/><br/><br/>
                <button>Submit</button>
            </form>
            {error&&<p>{error}</p>}
            {inputError&&<p>{inputError}</p>}
        </div>
    );
}
export default Weather;
